import React from 'react'
import './LedMatrix.css'
import { getNormalizedColor, ERROR_MESSAGE } from '../../helpers';

const LedMatrix = ({ leds=[] , className}) =>
    {
        leds.length > 0 ?
    <div className={`LedMatrix ${className}`}>
        {

            leds.map(color =>
            <Led color={getNormalizedColor(color)} />
            )
        }
    </div> :
    <div className="">
        {ERROR_MESSAGE}
    </div>
    }

const Led = ({color=""}) =>
    <div className="LedMatrix-led" style={color ? {backgroundColor:color}: {}}>

    </div>


export default LedMatrix
